<script lang="ts">
	import { onMount } from 'svelte';
  import OpenSeadragon from 'openseadragon';

    let viewer:OpenSeadragon.Viewer;
    let {styleClasses, currentMapSrc, currentMaskSrc, dmView}:{styleClasses: string, currentMapSrc:string, currentMaskSrc:string, dmView:boolean} = $props();
    onMount(() => {
      viewer = OpenSeadragon({
        id: "openSeadragonViewer",
        prefixUrl: "https://openseadragon.github.io/openseadragon/images/",
        //Initially load a simple full white image
        tileSources: {
          type: 'image',
          url: "data:image/png;base64,"
        }
      });
      //Whenever the image is opened sucessfully overlay the mask in the 2nd OpenSeadragon Viewer Layer. Opacity depending on player/DM view
      viewer.addHandler("open", () => {
        if(currentMaskSrc != "" && viewer){
          viewer.addTiledImage({
          tileSource: {
                  type: "image",
                  url: currentMaskSrc
                },
          opacity: dmView?0.5:1, // adjust transparency (0 = fully transparent, 1 = fully opaque)
          });
        }
      });
    });
    //Whenever currentMapSrc or currentMaskSrc is updated (meaning a new map image or Mask Image has been loaded), open the image in the OpenSeadragon Viewer
    $effect(() => {
      //I only print to console so the $effect tracks both variables. Without this line the effect does for some reason not trigger when only currentMaskSrc changes. Maybe a svelt pro can educate me...
      console.log("map:",currentMapSrc,"mask:", currentMaskSrc);
      if (viewer){
        if(currentMapSrc != "" || currentMaskSrc != ""){
          openImage(viewer);
        }
      }
    });
    //Opens the image loaded at currentMapSrc in the OpenSeadragon Viewer
    function openImage(viewer:OpenSeadragon.Viewer){
      if((currentMapSrc != "" || currentMaskSrc != "") && viewer ){
        viewer.open({
                type: 'image',
                url: currentMapSrc
            })
      }
    }
    
</script>
<div id="openSeadragonViewer" class={styleClasses}>

</div>